        choix : 0,
        montab: tabImg[choix]
    })
    function mafonction() {
        carousel.choix = carousel.choix  + 1
        setcarousel({
            choix : carousel.choix
        })
        console.log(carousel.choix);
    }
    return (
        <div className={carousel.image} >
            <button onClick={() => mafonction()}>ici cliquezzzz !!</button>
        </div>
    );
}
export default Carousel;
