import { useState } from "react";
import "../style/carousel.css"
const Carousel = () => {
    let tabImg = ["img1", "img2", "img3"]
    let [carousel, setcarousel] = useState({
        choixIndex: 0,
        image: tabImg[carousel.choixIndex]
    })
    function mafonction(_x) {
        setcarousel({
            choixIndex: carousel.choixIndex ++,
            image: tabImg[_x]
        })
    }
    return (
        <div className={carousel.image} >
            <button onClick={() => mafonction(carousel.choixIndex)}>ici cliquezzzz !!</button>
        </div>
    );
}
export default Carousel;
